/* eslint-disable sonarjs/cognitive-complexity */
import { fileURLToPath } from "url";
import path from "path";
import fs from "fs/promises";
import minimist from "minimist";
import ejs from "ejs";
import yaml from "js-yaml";
import dotenv from "dotenv";

// Load .env file silently
const dotenvResult = dotenv.config({ path: path.resolve(".env") });
const loadedEnv = dotenvResult.parsed || {};

/**
 * Main entrypoint for CLI commands.
 * Dispatches commands to dedicated handlers.
 * @param {string[]} argv - Command-line arguments (excluding node and script path).
 */
export async function main(argv) {
  const args = minimist(argv, {
    boolean: ["help", "mission", "version", "render", "features"],
    alias: { h: "help", m: "mission", f: "features", e: "env", y: "yaml2json", o: "output" },
    string: ["env", "yaml2json", "output"]
  });

  // Handle environment variables option
  if (args.env !== undefined) {
    handleEnv(args.env);
    return;
  }

  if (args.help) {
    return printHelp();
  }
  if (args.mission) {
    return printMission();
  }
  if (args.version) {
    return printVersion();
  }
  if (args.features) {
    return listFeatures(args);
  }
  if (args.render) {
    return renderTemplate(args);
  }
  if (args.yaml2json !== undefined) {
    return convertYamlToJson(args.yaml2json, args.output);
  }
  if (args._.length > 0 && args._[0] === "plot") {
    return plotCommand(args);
  }
  if (args._.length > 0 && args._[0] === "echo") {
    return echoMessage(args._);
  }

  // Default to help when no known command is provided
  return printHelp();
}

/**
 * Handle --env and -e option.
 * @param {boolean|string} envArg - The argument passed to --env.
 */
function handleEnv(envArg) {
  if (envArg === true || envArg === "") {
    console.log(JSON.stringify(loadedEnv, null, 2));
    process.exit(0);
  }
  const varName = envArg;
  const value = loadedEnv[varName];
  if (value === undefined) {
    console.error(`Missing environment variable: ${varName}`);
    process.exit(1);
  }
  console.log(value);
  process.exit(0);
}

/**
 * Print usage instructions and summary of commands.
 */
function printHelp() {
  const cmd = path.basename(process.argv[1]);
  console.log(
    `Usage: ${cmd} [--help] [-m|--mission] [--version] [-f|--features <tag>...] [--render <template> <data>] [--env <VAR_NAME>] [--yaml2json <yamlPath>] [--output <file>] [plot <quadratic|sine>] [echo <message>]`
  );
  console.log("");
  console.log("Commands:");
  console.log("  --help                      Display usage instructions");
  console.log("  -m, --mission               Print mission statement");
  console.log("  --version                   Print version");
  console.log("  -f, --features [<tag>...]   List available features, optionally filtered by mission tags");
  console.log("  --render <template> <data>  Render EJS template with data (JSON or YAML)");
  console.log("  --yaml2json <yamlPath>      Convert YAML file to JSON printed to stdout");
  console.log("  -y <yamlPath>               Alias for --yaml2json");
  console.log("  --output <file>             Write JSON output to the specified file");
  console.log("  -e, --env <VAR_NAME>        Print a specific environment variable");
  console.log("  -e, --env                   Print all loaded environment variables as JSON");
  console.log("  plot <quadratic|sine>       Generate sample points for the specified function");
  console.log("  echo <message>              Echo message");
}

/**
 * Print the mission statement from MISSION.md.
 */
async function printMission() {
  const mission = await fs.readFile(path.resolve("MISSION.md"), "utf-8");
  console.log(mission);
}

/**
 * Print the version from package.json.
 */
async function printVersion() {
  const pkg = JSON.parse(
    await fs.readFile(path.resolve("package.json"), "utf-8")
  );
  console.log(pkg.version);
}

/**
 * List available features by reading markdown files and extracting YAML frontmatter,
 * optionally filtering by provided mission tags.
 * @param {object} args - Parsed CLI arguments including positional tags in args._
 */
async function listFeatures(args) {
  const missionText = await fs.readFile(path.resolve("MISSION.md"), "utf-8");
  console.log(missionText);

  try {
    const dirPath = path.resolve("sandbox/features");
    const files = await fs.readdir(dirPath);
    const mdFiles = files.filter((f) => f.endsWith(".md"));
    const featuresList = [];

    for (const file of mdFiles) {
      const raw = await fs.readFile(path.join(dirPath, file), "utf-8");
      let mission = [];
      let content = raw;
      // Parse YAML frontmatter if present
      if (content.startsWith("---")) {
        const fmMatch = content.match(/^---\s*[\r\n]+([\s\S]*?)\r?\n---[\r\n]+/);
        if (fmMatch) {
          try {
            const fmData = yaml.load(fmMatch[1]);
            if (fmData && Array.isArray(fmData.mission)) {
              mission = fmData.mission;
            }
          } catch (e) {
            // ignore YAML parse errors
          }
          content = content.slice(fmMatch[0].length);
        }
      }

      // Extract title
      // eslint-disable-next-line sonarjs/slow-regex
      const titleMatch = content.match(/^#\s+(.*)$/m);
      const title = titleMatch ? titleMatch[1].trim() : "";
      // Extract description: first non-heading paragraph
      // eslint-disable-next-line sonarjs/slow-regex
      const afterHeading = content.replace(/^#\s+.*$/m, "").trim();
      let description = "";
      if (afterHeading) {
        const lines = afterHeading.split(/\r?\n/);
        const descLines = [];
        for (const line of lines) {
          if (line.trim() === "") break;
          descLines.push(line.trim());
        }
        description = descLines.join(" ");
      }
      featuresList.push({ title, description, mission });
    }

    const filterTags = Array.isArray(args._) ? args._ : [];
    let outputList = featuresList;
    if (filterTags.length > 0) {
      outputList = featuresList.filter((f) =>
        Array.isArray(f.mission) && f.mission.some((tag) => filterTags.includes(tag))
      );
    }

    console.log(JSON.stringify(outputList, null, 2));
    process.exit(0);
  } catch (e) {
    console.error(`Error: ${e.message}`);
    process.exit(1);
  }
}

/**
 * Render an EJS template with provided JSON or YAML data.
 */
async function renderTemplate(args) {
  const [templatePath, dataPath] = args._;
  if (!templatePath || !dataPath) {
    console.error("Error: --render requires <templatePath> and <dataPath>");
    process.exit(1);
  }
  const tpl = await fs.readFile(path.resolve(templatePath), "utf-8");
  const dataRaw = await fs.readFile(path.resolve(dataPath), "utf-8");
  let data;
  if (dataPath.endsWith(".json")) {
    data = JSON.parse(dataRaw);
  } else if (dataPath.endsWith(".yaml") || dataPath.endsWith(".yml")) {
    data = yaml.load(dataRaw);
  } else {
    data = JSON.parse(dataRaw);
  }
  const output = ejs.render(tpl, data);
  console.log(output);
}

/**
 * Convert a YAML file to JSON, writing to stdout or an output file.
 * @param {string} yamlPath - Path to the input YAML file.
 * @param {string} outputPath - Optional path to write the JSON output.
 */
async function convertYamlToJson(yamlPath, outputPath) {
  try {
    const raw = await fs.readFile(path.resolve(yamlPath), "utf-8");
    let data;
    try {
      data = yaml.load(raw);
    } catch (err) {
      console.error(err.message);
      process.exit(1);
    }
    const json = JSON.stringify(data, null, 2);
    if (outputPath) {
      try {
        await fs.writeFile(path.resolve(outputPath), json, "utf-8");
      } catch (err) {
        console.error(err.message);
        process.exit(1);
      }
    } else {
      console.log(json);
    }
    process.exit(0);
  } catch (err) {
    console.error(err.message);
    process.exit(1);
  }
}

/**
 * Plot command for quadratic and sine sample data.
 * @param {object} args - Parsed CLI arguments including positional args in args._
 */
function plotCommand(args) {
  const funcName = args._[1];
  if (!funcName) {
    console.error("Missing function name");
    process.exit(1);
  }
  let points;
  if (funcName === "quadratic") {
    const xs = [-2, -1, 0, 1, 2];
    points = xs.map((x) => ({ x, y: x * x }));
  } else if (funcName === "sine") {
    const xs = [-Math.PI, -Math.PI / 2, 0, Math.PI / 2, Math.PI];
    points = xs.map((x) => ({ x, y: Math.sin(x) }));
  } else {
    console.error(`Unknown function: ${funcName}`);
    process.exit(1);
  }
  console.log(JSON.stringify(points, null, 2));
  process.exit(0);
}

/**
 * Echo message arguments to stdout.
 */
function echoMessage(args) {
  console.log(args.slice(1).join(" "));
}

// If the script is run directly, invoke main with process arguments
if (process.argv[1] === fileURLToPath(import.meta.url)) {
  main(process.argv.slice(2));
